import logging
import ccx2paraview
import sys
import os
logging.basicConfig(level=logging.INFO, format='%(levelname)s: %(message)s')
num_iter = int(sys.argv[1])
opt_path = sys.argv[2]
for i in range(1,num_iter+1):
    file_i = os.path.join(opt_path, "file" + str(i).zfill(3)) + '.frd'
    c = ccx2paraview.Converter(file_i, ['vtu'])
    c.run()
