import os
from ..path import p
def read_config():
    if os.path.exists(p.user_settings):
        with open(p.user_settings, 'r') as f:
            for line in f.readlines():
                if(line.find('self.start_model') == 0):
                    model = line.split()[2]
                    model = model[1:-5] # *.inp
                    print('current_moel',model)
                if(line.find('self.working_dir') == 0):
                    current_working_dir = line.split()[2]
                    current_working_dir = current_working_dir[1:-1]
                    print('current_working_dir:',current_working_dir)
        return model,current_working_dir
    else:
        return False